import axios from 'axios';

export const fetchLyric = id => (dispatch) =>{
    axios.get(`http://localhost:4000/playlist/detail?id=${id}`).then((res)=>{
        if(res.data.code){
            dispatch();
        }
        console.log(res.data.code);
    })
};
