import React, { Component } from 'react';
import { Icon } from 'antd';
import MinorTitle from '@components/MinorTitle'
import { formatTimeStamp } from '@src/utils'
import './style.less';

const renderCreator = playlist =>(
    <div className="creator">
        <span className="creator-avatar">
            <img src={playlist.creator.avatarUrl} alt="creator-avatar" />
        </span>
        <span className="creator-nickname">{playlist.creator.nickname}</span>
        <span className="create-time">{formatTimeStamp(playlist.createTime)}ÂàõÂª∫</span>
        <span className="play-count">üéß{playlist.playCount}</span>
    </div>
)
class Header extends Component {
    constructor(props){
        super(props);
        this.state = {
            showDesc:false
        }
    }
    render(){
        const { playlist } = this.props;
        const {coverImgUrl, name, description, tags} = playlist
        console.log(playlist);
        return(
            <div>
                <MinorTitle>Ê≠åÂçï</MinorTitle>
                <div className="info-header">
                    <div className="cover-img">
                        <img src={coverImgUrl} alt="cover-img"/>
                    </div>
                    <div className="right">
                        <p className="title">{name}</p>
                        {renderCreator(playlist)}
                        <div className="operation-buttons"></div>
                        <div className="tags">
                            Ê†áÁ≠æÔºö
                            {tags.map(v => <span key={v}>{v}</span>)}
                        </div>
                        <div className="playlist-desc">
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Header;
