import axios from 'axios';
import { CARD_LIST, SWIPER_IMG } from './actionTypes'
import { topPlaylist, bannerUrl, test } from '../../../config/api'

export const getCardListAction = (list) =>({
    type: CARD_LIST,
    payload: { list }
})

export const fetchCardList = () => (dispatch) => {
    axios.get('https://api.imjad.cn/cloudmusic/?type=playlist&id=309390784').then((res) =>{
        const { data } = res;
        var result=[]
        for (var i = 6; i > 0; i--) {
            result[i]=data
        }
        if(result.code) {
            console.log(123);
            dispatch(getCardListAction(data.playlists))
        }else{
            dispatch(getCardListAction(data.playlists))
        }
    })

}
