    size: 6553434,
    vd: -2,
  },
  a: null,
  cd: '1',
  no: 18,
  rtUrl: null,
  ftype: 0,
  rtUrls: [],
  djId: 0,
  copyright: 2,
  s_id: 0,
  mst: 9,
  cp: 0,
  mv: 0,
  rtype: 0,
  rurl: null,
  publishTime: 1193328000000,
  url: 'm10.music.126.net/20181222143245/e725ffc6f708bb8afc04723bff63fec6/ymusic/4501/61c6/dedf/3da99dc286eeefbdc5a3fbd9906e5fc3.mp3',
}

const initState = {
  playlist: [song],
  song,
  index: 0,
  flag: '',
  currentTime: 0,
  isPlaying: false,
  starredList: [],
}

export default (state = initState, action) =>{
    // let delIndex;
    // const stateTemp = JSON.parse(JSON.stringify(state));
    // const { playlist } = stateTemp;
    // const len = playlist.length;
    switch (action.type) {
        case actionTypes.INIT_STARRED_LIST:
            return { ...state, starredList: [...action.payload] }
        case actionTypes.PLAYER_UPDATE_STATUS:
            return {
                ...state,
                ...action.payload
            }
        default:
            return state;
    }
}
