    constructor(props){
        super(props);
        this.state={
            activeLines: [],
            top: 40
        }
    }
    componentDidMount(){

    }
    render(){
        const { lyric } = this.props;
        return (
            <div className="lyric-block">
                {lyric}
            </div>
        )
    }
}
