import * as actionTypes from './actionTypes';

const song = {
  name: 'だんご大家族',
  id: 760533,
  pst: 0,
  t: 0,
  artists: [
    {
      id: 17601,
      name: '茶太',
      tns: [],
      alias: [],
    },
  ],
  alia: [],
  pop: 100,
  st: 0,
  rt: '',
  fee: 0,
  v: 51,
  crbt: null,
  cf: '',
  album: {
    id: 760533,
    name: 'メグメル/だんご大家族',
    picUrl: 'https://p1.music.126.net/jNhg3v8ayCzL_k470qQyvQ==/3424978721550752.jpg',
    tns: [],
    pic_str: '18225504742452490',
    pic: 3424978721550752,
  },
  dt: 184737,
  h: null,
  h: {
    br: 320000,
    fid: 0,
    size: 10922361,
    vd: -2,
  },
  l: {
    br: 96000,
    fid: 0,
    size: 2217527,
    vd: -2,
  },
  a: null,
  cd: '1',
  no: 18,
  rtUrl: null,
  ftype: 0,
  rtUrls: [],
  djId: 0,
  copyright: 2,
  s_id: 0,
  mst: 9,
  cp: 0,
  mv: 0,
  rtype: 0,
  rurl: null,
  publishTime: 1193328000000,
  url: 'https://m7.music.126.net/20181222132022/4da9b595b910fb5b1730ef65be65f52a/ymusic/9bf8/4962/8e74/554b479c2a14973d2c6ad78d9d87c6ae.mp3',
}

const initState = {
  playlist: [song],
  song,
  index: 0,
  flag: '',
  currentTime: 0,
  isPlaying: false,
  starredList: [],
}

export default (state = initState, action) =>{
    // let delIndex;
    // const stateTemp = JSON.parse(JSON.stringify(state));
    // const { playlist } = stateTemp;
    // const len = playlist.length;
    switch (action.type) {
        case actionTypes.INIT_STARRED_LIST:
            return { ...state, starredList: [...action.payload] }
        case actionTypes.PLAYER_UPDATE_STATUS:
            return {
                ...state,
                ...action.payload
            }
        default:
            return state;
    }
}
